mapper.select:
SELECT o.order_no, o.order_name, o.order_status, o.total_amount, TO_CHAR(o.order_date, 'YYYY-MM-DD HH24:MI') as order_date, ( SELECT LISTAGG(p.item_name, ', ') WITHIN GROUP (ORDER BY p.item_name) FROM order_detail od JOIN product p ON od.item_no = p.item_no WHERE od.order_no = o.order_no ) as item_names FROM orders o WHERE 1=1
AND o.order_no = #{orderNo}
AND o.order_name LIKE '%' || #{customerName} || '%'
AND o.order_status = #{status}
ORDER BY o.order_date DESC

mapper.update:
UPDATE product p
SET p.stock_cnt = p.stock_cnt + (
    SELECT od.item_cnt
    FROM order_detail od
    WHERE od.order_no = #{orderNo}
    AND od.item_no = p.item_no
)
WHERE p.item_no IN (
    SELECT item_no FROM order_detail WHERE order_no = #{orderNo}
)

UPDATE product p
SET p.stock_cnt = p.stock_cnt - (
    SELECT od.item_cnt
    FROM order_detail od
    WHERE od.order_no = #{orderNo}
    AND od.item_no = p.item_no
)
WHERE p.item_no IN (
    SELECT item_no FROM order_detail WHERE order_no = #{orderNo}
)

UPDATE product
SET stock_qty = stock_qty + #{item_cnt}
WHERE item_no = #{item_no}

UPDATE product
SET stock_qty = stock_qty - #{item_cnt}
WHERE item_no = #{item_no}
