SELECT * FROM ( SELECT a.*, ROWNUM rn FROM ( SELECT item_no AS item_no, item_img AS item_img, item_name AS item_name, item_content AS item_content, origin_p AS origin_p, sales_p AS sales_p, stock_cnt AS stock_cnt, cate_no AS cate_no, dis_no AS dis_no FROM product
(item_name LIKE '%' || #{q} || '%' OR item_content LIKE '%' || #{q} || '%')
AND cate_no = #{cate_no}
ORDER BY sales_p ASC ORDER BY sales_p DESC ORDER BY item_no DESC
) a WHERE ROWNUM <= #{offset} + #{size} ) WHERE rn > #{offset}

